import React, { useEffect } from "react";

export default function Login() {
  useEffect(() => {
    const redDiv = document.querySelector(".col-6");
    const imgL = document.getElementById("LoginImg");
    const rect = redDiv.getBoundingClientRect();
    const distanceFromTop = rect.top;
    console.log(distanceFromTop);
    redDiv.style.height = `calc(100vh - ${distanceFromTop}px)`;
    imgL.style.maxHeight = redDiv.style.height;
    imgL.style.maxWidth = redDiv.style.width;
    console.log(redDiv.style.height);
  }, []);

  return (
    <div style={{ display: "flex", flexGrow: 1 }}>
      <div className="col-6" style={{ background: "#CBE4E8" }}>
        <img src="shopping-transparent-background-13.png" className="LoginImg"/>
      </div>
    </div>
  );
}
