import React from 'react'
import CartItem from './CartItem'


export default function Cart() {
    const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const addToCart = (item) => {
    console.log(cart);
    setCart(prevCart => {
      const existingItem = prevCart.find((cartItem) => cartItem._id === item._id);
      console.log(item._id);
  
      if (existingItem) {
        return prevCart.map((cartItem) =>
          cartItem._id === item._id
            ? { ...cartItem, quantity: cartItem.quantity + 1 }
            : cartItem
        );
      } else {
        return [...prevCart, { ...item, quantity: 1 }];
      }
    });
  };
  
  const removeFromCart = (item) => {
    setCart(prevCart => {
      const updatedCart = prevCart.map((cartItem) =>
        cartItem._id === item._id
          ? {
              ...cartItem,
              quantity: Math.max(0, cartItem.quantity - 1),
            }
          : cartItem
      );
  
      return updatedCart;
    });
  };
  
  return (
    <>
    <div className='CartDiv row row-ow'>
        <div className="col-3 CartHeadings"><b>Product</b></div>
        <div className="col-3 CartHeadings"><b>Price</b></div>
        <div className="col-3 CartHeadings"><b>Quantity</b></div>
        <div className="col-3 CartHeadings"><b>Subtotal</b></div>
    </div>
    <div className='CartDiv row row-ow'>
        <CartItem></CartItem>
    </div>
    </>
  )
}
